parameters.functionCode:
const input = $json.body?.data;

if (!Array.isArray(input)) {
  throw new Error("Invalid input data. Expected 'data' to be an array.");
}

return input.map(company => ({
  json: {
    name: company.name,
    linkedinUrl: company.linkedinUrl,
    email: company.email,
  }
}));

